$("#stackedSlideshow").on("slide.bs.carousel", function (e) {
    var direction = e.direction === "left" ? "next" : "prev";
    var items = $(this).find(".carousel-item");
    items.css("z-index", 1);
    $(e.relatedTarget).css("z-index", 3);
    var activePos = $(e.relatedTarget).index();
    var nextPrevItem = items.eq(
      direction === "next"
        ? (activePos + 1) % items.length
        : (activePos - 1 + items.length) % items.length
    );
    nextPrevItem.css("z-index", 2);
  });